        return new Source("{% extends 'base.html.twig' %}

{% block title %}Reset Password{% endblock %}

{% block stylesheets %}
    {{ parent() }}
    <link rel=\"stylesheet\" href=\"{{ asset('css/login.css') }}\">
{% endblock %}

{% block body %}

    {% for message in app.flashes('notice') %}
        <div class=\"alert alert-info\">
            {{ message }}
        </div>
    {% endfor %}
    
   <form method=\"post\">
    
        <h1 class=\"h3 mb-3 font-weight-normal\">Email</h1>
        <label for=\"inputEmail\">Email</label>
        <input type=\"email\" name=\"email\" id=\"inputEmail\" class=\"form-control\" required autofocus>
        <br>

        <button class=\"btn btn-lg btn-primary\" type=\"submit\">
            Reset
        </button>
        
        <p>Or Go back<a href=\"{{ path('app_login') }}\">to login</a>.</p>
        {# <p>Or Go back<a href=\"/login_1\">to login</a>.</p> #}

    </form>

{% endblock %}
